def big(event):
    search = event.parsed.rest
    for line in wet_tekst.split("\n\n"):
        if event.parsed.rest:
            if event.parsed.rest in line: event.reply(line) ; event.reply("")
        else: kernel.announce(line)

main.register("big", big)

